mapper.select:
SELECT *
FROM TB_EXCH_RATE_HIST
WHERE rhist_currency = #{currency}
  AND rhist_reg_dt = (
    SELECT MAX(rhist_reg_dt)
    FROM TB_EXCH_RATE_HIST
    WHERE rhist_currency = #{currency}
)
AND ROWNUM = 1

mapper.update:
UPDATE TB_CUST_ACCT
SET acct_balance = #{balance}
WHERE acct_no = #{acctNo}

UPDATE TB_FRGN_ACCT_BALANCE
SET bal_balance = #{balance}
WHERE bal_no = #{balNo}

mapper.select:
SELECT cust_name
FROM TB_CUST_INFO
WHERE cust_code = #{custCode}

SELECT
    *
FROM TB_FRGN_ACCT_BALANCE
WHERE bal_frgn_acct_no = #{frgnAcctNo}
  AND bal_currency = #{currency}
